def crypte_lettre(lm, lc):
    a = ord(lm)
    b = ord(lc)
    c = a ^ b
    lettre = chr(c)
    
    return c

def crypte_mot(mot1, mot2):
    mot3 = []
    for i in range(len(mot1)):
        car = crypte_lettre(mot1[i],mot2[i])
        mot3.append(car)
    return mot3

